mapper.select:
SELECT * FROM item


        trade_mode = #{mode}


        and id != #{itemId}


        and tag = #{tag}


        and id not in

            #{id}


    and status = 0 and pageflag != 1

order by RAND() LIMIT 20

SELECT * FROM item


        trade_mode = #{mode}

    and owneruid = #{openId}
    and status = 0 and pageflag != 1

order by create_time
